import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HotToastService } from '@ngneat/hot-toast';
import { BloodBankService } from '../blood-bank.service';

@Component({
  selector: 'app-donor-home',
  templateUrl: './donor-home.component.html',
  styleUrls: ['./donor-home.component.css']
})
export class DonorHomeComponent {
  camps: any;

  constructor(
    private toast: HotToastService,
    private router: Router,
    private bloodBankService: BloodBankService
  ) {}

  ngOnInit(): void {
    this.postCamps();
  }

  postCamps(): void {
    this.bloodBankService.getCamps().subscribe(
      (data: any) => {
        this.camps = data
        console.log(data)
      }
    )
  }

  convertTo12HourFormat(timeString: any): string {
    const [hours, minutes] = timeString.split(":");
    let formattedTime: number;
    let period: string;

    if (hours < 12) {
      formattedTime = hours;
      period = "AM";
    } else {
      formattedTime = hours % 12;
      period = "PM";
    }

    return `${formattedTime}:${minutes} ${period}`;
  }

  isRegistered = false;
  buttonLabel = 'Register Yourself';

  onButtonClick(): void {
    if (this.isRegistered) {
      this.unregister();
    } else {
      this.register();
    }
  }

  register(): void {
    this.buttonLabel = 'Unregister Yourself';
    this.isRegistered = true;

  }

  unregister(): void {
    this.buttonLabel = 'Register Yourself';
    this.isRegistered = false;
  }
}
