import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HotToastService } from '@ngneat/hot-toast';
import { BloodBankService } from '../blood-bank.service';

@Component({
  selector: 'app-donor-home',
  templateUrl: './donor-home.component.html',
  styleUrls: ['./donor-home.component.css']
})
export class DonorHomeComponent {
  camps: any;

  constructor(
    private toast: HotToastService,
    private router: Router,
    private route: ActivatedRoute,
    private bloodBankService: BloodBankService
  ) {}

  ngOnInit(): void {
    this.postCamps();
  }

  postCamps(): void {
    this.bloodBankService.getCamps().subscribe(
      (data: any) => {
        this.camps = data
        console.log(data)
      }
    )
  }

  convertTo12HourFormat(timeString: any): string {
    const [hours, minutes] = timeString.split(":");
    let formattedTime: number;
    let period: string;

    if (hours < 12) {
      formattedTime = hours;
      period = "AM";
    } else {
      formattedTime = hours % 12;
      period = "PM";
    }

    return `${formattedTime}:${minutes} ${period}`;
  }


  isRegistered(camp: any): boolean {
    const campId = camp.id
    const donorId = this.route.snapshot.paramMap.get('id');
    // console.log(donorId);
    const item = {
      campId, donorId
    }
    // console.log(campId)
    this.bloodBankService.isRegister(item).subscribe(
      (data) => console.log(data)
    )
    return false;
  }

  onButtonClick(camp: any) {
    if (this.isRegistered(camp)) {
      this.unregisterCamp(camp);
    } else {
      this.registerCamp(camp);
    }
  }

  registerCamp(camp: any) {
  }

  unregisterCamp(camp: any) {

  }
}
