import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HotToastService } from '@ngneat/hot-toast';

@Component({
  selector: 'app-create-camp',
  templateUrl: './create-camp.component.html',
  styleUrls: ['./create-camp.component.css']
})
export class CreateCampComponent {
  addForm!: NgForm;
  submitted: boolean = false;

  constructor(
    private toast: HotToastService,
    private router: Router,
    private bloodBankService: BloodBankService
  ) {}

  onSubmit(item: NgForm) {
    this.submitted = true;
    if(item.invalid){
      this.toast.error('Cannot create camp with empty fieldsðŸ™„')
      return;
    }
    console.log(item.value);

    this.bloodBankService.createDonor(item).pipe(
      this.toast.observe({
        loading: 'Saving...',
        success: (s) => 'Registered Successfully',
        error: (e) => 'Cannot register with empty fieldsðŸ™„'
      })
    ).subscribe();
  }





  getMinDate(): string {
    const today = new Date();
    const year = today.getFullYear();
    let month: string | number = today.getMonth() + 1;
    let day: string | number = today.getDate();
    if (month < 10) {
      month = '0' + month;
    }
    if (day < 10) {
      day = '0' + day;
    }
    return `${year}-${month}-${day}`;
  }
}
