import { Component, AfterViewInit, OnDestroy } from '@angular/core';

@Component({
  selector: 'app-loading-animation',
  templateUrl: './loading-animation.component.html',
  styleUrls: ['./loading-animation.component.css']
})
export class LoadingAnimationComponent implements AfterViewInit, OnDestroy {
  private lines: SVGLineElement[] = [];
  private svg: SVGSVGElement | null = null;
  private intervalId: any;
  public isLoading: boolean = true; // Add the isLoading property

  ngAfterViewInit() {
    this.lines = Array.from(document.querySelectorAll<SVGLineElement>("#front line"));
    this.svg = document.getElementById("loader") as unknown as SVGSVGElement;
    const box = this.svg?.getBBox();
    this.svg?.setAttribute("viewBox", `${box?.x} ${box?.y} ${box?.width} ${box?.height}`);
    this.run();
    this.intervalId = setInterval(() => this.run(), 2000);
  }

  ngOnDestroy() {
    clearInterval(this.intervalId);
  }

  private run() {
    for (let i = 0; i < this.lines.length; i += 1) {
      const currLine = this.lines[i];
      this.setStyle(currLine, i);
      this.removeStyle(currLine, i);
    }
  }

  private setStyle(currElement: SVGLineElement, i: number) {
    setTimeout(() => {
      // currElement.setAttribute('style', `stroke: #ff4d4f; stroke-dasharray: ${currElement.getTotalLength()}px; stroke-dashoffset: ${currElement.getTotalLength()}px; animation: dash 0.1s ease-out forwards 0.1s; animation-delay: ${0.06 * i}s`);
      currElement.setAttribute('style', `stroke: #ff4d4f; stroke-dasharray: ${currElement.getTotalLength()}px; stroke-dashoffset: ${currElement.getTotalLength()}px; animation: dash 0.1s ease-out forwards 0.1s; animation-delay: ${0.06 * i}s`)
    }, i);
    }, i);
  }

  private removeStyle(element: SVGLineElement, i: number) {
    setTimeout(() => {
      element.removeAttribute("style");
    }, (100 * (i + 2)) - ((i + 1) * 20));
  }
}
