import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { DonorSignupComponent } from './donor-signup/donor-signup.component';
import { DonorHomeComponent } from './donor-home/donor-home.component';
import { AdminLoginComponent } from './admin-login/admin-login.component';
import { CreateCampComponent } from './create-camp/create-camp.component';
import { AdminHomeComponent } from './admin-home/admin-home.component';
import { EditCampComponent } from './edit-camp/edit-camp.component';

import { RoleGuard } from './role.guard';

const routes: Routes = [
  {
    path: '',
    redirectTo: '/donor-home',
    pathMatch: 'full',
  },
  {
    path: 'donor-home/:id',
    component: DonorHomeComponent,
    canActivate: [RoleGuard],
    data: {
      expectedRole: 'donor',
    },
  },
  {
    path: 'donor-signup',
    component: DonorSignupComponent,
  },
  {
    path: 'admin-login',
    component: AdminLoginComponent,
  },
  {
    path: 'create-camp',
    component: CreateCampComponent,
    canActivate: [RoleGuard],
    data: {
      expectedRole: 'admin',
    },
  },
  {
    path: 'admin-home',
    component: AdminHomeComponent,
    canActivate: [RoleGuard],
    data: {
      expectedRole: 'admin',
    },
  },
  {
    path: 'edit-camp/:id',
    component: EditCampComponent,
    canActivate: [RoleGuard],
    data: {
      expectedRole: 'admin',
    },
  },
  {
    path: '**',
    redirectTo: '/donor-home',
  },
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule],
})
export class AppRoutingModule {}
